#!/usr/bin/env python3

sequencia = ""

#iterando e contando cada um dos nucleotideos presentes na sequência
#armazenando os valores das contagens em diferentes objetos

count_c = 0
count_g = 0
count_a = 0
count_t = 0

for i in range(len(sequencia)):
  list_str = list(set(sequencia))
  if sequencia[i] == list_str[0]:
    count_c+=1
  if sequencia[i] == list_str[1]:
    count_g+=1
  if sequencia[i] == list_str[2]:
    count_a+=1
  if sequencia[i] == list_str[3]:
    count_t+=1

contagens_nt = { 'C' : count_c, 'G' : count_g, 'A' : count_a, 'T' : count_t}
print(sequencia,"\n",contagens_nt)

#GC content

GC_cont = count_g + count_c
print("o conteúdo de GC é:", GC_cont)


